import React, { useEffect, useState } from "react";

const BookReview = (props) => {
  const {
    match: {
      params: { reviewId },
    },
  } = props;
  const [review, setReview] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/reviews")
      .then((res) => res.json())
      .then((allReviews) => {
        let selectedReview = [];
        // allReviews.map((newReview) => {
        //   if (newReview.id === parseInt(reviewId)) selectedReview.push(newReview);
        // });
        // console.log("selectedReview--", selectedReview);
        // setReview(selectedReview);
        const filteredReview = allReviews.filter(
          (newReview) => newReview.id === parseInt(reviewId)
        );
        setReview(filteredReview);
        console.log("dssdssdsds--", review);
      });
  }, [props, reviewId]);

  const getLikes = (review) => {
    console.log("review=========", review);
    if (review.like > 0) return `Like (${review.like})`;
    return "Like";
  };

  const getDisLikes = (review) => {
    if (review.dislike > 0) return `DisLike (${review.dislike})`;
    return "DisLike";
  };

  const displayAdditionalComments = (review) => {
    return review.comments.length > 0 ? (
      <>
        {review.comments.map((comment) => {
          <>
            <h2>comment.name</h2>
            <h2>comment.published</h2>
            <p>comment.text</p>
          </>;
        })}
      </>
    ) : null;
  };

  return (
    <div className="Home">
      <div>
        {review.length > 0 ? (
          <div className="row">
            <div className="imgCol" style={{ marginTop: 30 }}>
              <div className="image">
                <img
                  src="https://books.google.com/books/content?id=VQewd9LDbzgC&printsec=frontcover&img=1&zoom=1&source=gbs_api"
                  style={{ width: 175, height: 250 }}
                />
              </div>
              <div className="imageText" style={{ marginTop: 30 }}>
                <h2>Desert Solitaire</h2>
                <p>Edward Abbey</p>
                <p>Biography & Memoir</p>
                <button
                  className="textBtn"
                  style={{ marginTop: 10 }}
                  disabled={true}
                >
                  {getLikes(review)}
                </button>
                <button
                  className="textBtn"
                  style={{ float: "right" }}
                  disabled={true}
                >
                  {getDisLikes(review)}
                </button>
                <div>
                  <div style={{ color: "#A9A9A9" }}>'review.reviewer.name'</div>
                  <div>review.reviewer.published</div>
                </div>
                <div className="comment">review.reviewer.text</div>
                {displayAdditionalComments(review)}
              </div>
            </div>
          </div>
        ) : null}
      </div>
    </div>
  );
};

export default BookReview;
