import React, { useEffect, useState } from "react";

const BookReview = (props) => {
  const {
    match: {
      params: { reviewId },
    },
  } = props;
  const [reviews, setReviews] = useState([]);
  const [review, setReview] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/reviews")
      .then((res) => res.json())
      .then((allReviews) => {
        // setReviews(res);
        const selectedReview = allReviews.find(
          (review) => review.Id === parseInt(reviewId)
        );
        setReviews(reviews.filter((data) => data.id !== parseInt(reviewId)));
        setReview(selectedReview);
      });
  }, [props, reviewId]);

  const getLikes = (review) => {
    if (review.like > 0) return `Like (${review.like})`;
    return "Like";
  };

  const getDisLikes = (review) => {
    if (review.dislike > 0) return `DisLike (${review.dislike})`;
    return "DisLike";
  };

  return (
    <div className="Home">
      <div>
        {review && (
          <div className="row">
            <div className="imgCol" style={{ marginTop: 30 }}>
              <div className="image">
                {/* <img
                  src={review.book.image}
                  style={{ width: 175, height: 250 }}
                /> */}
              </div>
              <div className="imageText" style={{ marginTop: 30 }}>
                {/* {/* <h2>{review.book.title}</h2> */}
                <p>{review}</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookReview;
