import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

const Home = (props) => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/reviews")
      .then((res) => res.json())
      .then((res) => {
        setReviews(res);
      });
  }, []);

  const getLikes = (review) => () => {
    if (review.like > 0) return `Like (${review.like})`;
    return "Like";
  };

  const getDisLikes = (review) => () => {
    if (review.dislike > 0) return `DisLike (${review.dislike})`;
    return "DisLike";
  };

  const handleReviewClick = (reviewId) => () => {
    const url=`/review/${reviewId}`;
    props.history.push(url);
  };

  return (
    <div className="Home">
      <div>
        {reviews.map((review) => (
          <div
            className="row"
            key={review.id}
            className="review"
            onClick={handleReviewClick(review.Id)}
          >
            <div className="imgCol">
              <h2>{review.book.title}</h2>
              <p>{review.book.author}</p>
            </div>
            <div className="commentCol">
              <h2>{review.reviewer.name}</h2>
              <p>{review.reviewer.text}</p>
            </div>
            <div className="buttonCol">
              <button disabled={true}>{getLikes(review)}</button>
              <button disabled={true}>{getDisLikes(review)}</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default withRouter(Home);
