import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom";

const BookReview = (props) => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/reviews")
      .then((res) => res.json())
      .then((res) => {
        setReviews(res);
      });
  }, []);

  const getLikes = (review) => {
    if (review.like > 0) return `Like (${review.like})`;
    return "Like";
  };

  const getDisLikes = (review) => {
    if (review.dislike > 0) return `DisLike (${review.dislike})`;
    return "DisLike";
  };

  return (
    <div className="Home">
      <div>
        {reviews.map((review) => (
          <div className="row">
            {/* <div className="column" style="background-color:#aaa;">
              <h2>{review.book.title}</h2>
              <p>{review.book.author}</p>
            </div>
            <div className="column" style="background-color:#bbb;">
              <h2>{review.reviewer.name}</h2>
              <p>{review.reviewer.text}</p>
            </div>
            <div className="column" style="background-color:#ccc;">
              <button disabled={true}>{this.getLikes(review)}</button>
              <button disabled={true}>{this.getDisLikes(review)}</button>
            </div> */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default withRouter(BookReview);
